
var tag = document.createElement('script');

tag.src = "https://www.youtube.com/iframe_api";
var firstScriptTag = document.getElementsByTagName('script')[0];
firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

// 3. This function creates an <iframe> (and YouTube player)
//    after the API code downloads.
var player;

function onYouTubeIframeAPIReady() {

    if (!window.newlistener) return;
    if (localStorage.getItem('autoplayed') == 'true') return;
    localStorage.setItem('autoplayed', 'true');
    debugger;
    player = new YT.Player('logo_inner', {
        height: '390',
        width: '640',
        //origin: 'http://listricity.com',
        videoId: 'VSQ_QVsUP7s',
        playerVars: {
            'autoplay': 1,
            'controls': 1,
            'autohide': 1,
            'wmode': 'opaque',
            'iv_load_policy': 3,
            'modestbranding': 1,
            'showinfo': 0,
            'rel': 0
        },
        events: {
            //'onReady': function(){debugger;event.target.playVideo();},
            'onStateChange': onPlayerStateChange
        }
    });


}

// 4. The API will call this function when the video player is ready.
function onPlayerReady(event) {
    // event.target.playVideo();
}
var unstartetimer = false;
var unstartedconcecutives = 0;
var lastplayednexttime = new Date();
function onPlayerStateChange(event) {
    console.log('onPlayerStateChange:' + player.getCurrentTime() + ' - ' + player.getDuration() + ' ' + event.data + ' ' + new Date());
    setTimeout("$('#logo_inner').css({'transform':'rotateY( 0deg )  scale(1)','opacity':'1','height':'100%','width':'100%'}).attr('width','').attr('height','');$('#logo').css({'background-size':'0px'});", 900);
    player.setLoop(false)
    //$('#logo_inner').css('width','100%').css('height','100%')
    //console.log(event.data);
    if (unstartetimer) clearTimeout(unstartetimer);
    if (event.data == YT.PlayerState.PAUSED) {
        $('button[onclick="stopVid()"]').html('Play').addClass('playbutton');
    }
    if (event.data == YT.PlayerState.UNSTARTED) {
        console.log('onPlayerStateChange:' + 'unstarted' + ' ' + new Date());
        unstartetimer = setTimeout(function () {
            unstartetimer = false;
            //if (unstartedconcecutives++ < 5);
            console.log('onPlayerStateChange:' + 'unstarted' + ' playnext ' + new Date());
            playNext()
        }, 3000);
    }
    if (event.data == YT.PlayerState.ENDED
        || (event.data == YT.PlayerState.BUFFERING && player.getCurrentTime() - player.getDuration() > -5 && player.getDuration() > 0) // anti repeat hack - any event in the last 5 seconds is ENDED
        || (event.data == YT.PlayerState.BUFFERING && player.getCurrentTime() < 2 && new Date() - lastplayednexttime > 10000) // anti repeat hack - any event in the last 5 seconds is ENDED
        ) {
        if (window.newlistener) {
            window.newlistener = false
        }
        else if(!neverplayed){
            console.log('onPlayerStateChange:' + player.getCurrentTime() + ' - ' + player.getDuration() + (event.data != YT.PlayerState.ENDED ? 'HACKED' : 'normal') + ' ended' + ' playnext ' + new Date());
            playNext();
        }
    }
    if (event.data == YT.PlayerState.PLAYING) {
        neverplayed = false;
        unstartedconcecutives = 0;
        $('#initvideo').fadeOut();
        $('button[onclick="stopVid()"]').html('Stop').removeClass('playbutton');
    }
}
var neverplayed = true;
function stopVideo() {
    player.stopVideo();
}

var autoplay=false;
function detect_autoplay(acceptable_delay){

    autoplay = false;  

    var autoplay_test_content = document.createElement('video');

   //create mp4 and webm sources, 5s long
        var mp4 = document.createElement('source');
        mp4.src = "data:video/mp4;base64,";

        var webm = document.createElement('source');
        webm.src = "data:video/webm;base64,";

   //append sources to test video 
        autoplay_test_content.appendChild(webm);
        autoplay_test_content.appendChild(mp4);

  //set attributes -needs to be visible or IE squawks, so we move it way outside  
    autoplay_test_content.id = "base64_test_video";
    autoplay_test_content.autoplay = true;
    autoplay_test_content.style.position = "fixed";
    autoplay_test_content.style.left = "5000px";

  //add to DOM       
    document.getElementsByTagName("body")[0].appendChild(autoplay_test_content);


    var base64_test_video = document.getElementById("base64_test_video");

  //test for autoplay, 100 ms buffer   
    setTimeout(function(){
        if(!base64_test_video.paused){
            autoplay = true;
        }

        document.getElementsByTagName("body")[0].removeChild(autoplay_test_content);

    },acceptable_delay);

}





